import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import CameraIcon from '@mui/icons-material/PhotoCamera';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Link from '@mui/material/Link';
import DirectionsCarIcon from '@mui/icons-material/DirectionsCar';
import {createTheme, ThemeProvider} from '@mui/material/styles';
import background from "../images/background.jpg";
import {Car, Country} from "../Models/Country";
import NavBar from "./NavBar";
import {useEffect, useState} from "react";
import {FormControl, InputLabel, MenuItem, Select, SelectChangeEvent} from "@mui/material";
import TextField from "@mui/material/TextField";
import {useParams, useSearchParams} from "react-router-dom";

function Copyright() {
  return (
    <Typography variant="body2" color="text.secondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://mui.com/">
        ReadFic
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}


const theme = createTheme();

export default function Search() {
  const [data, setData] = useState(Array<Car>());
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [fandoms, setCountrys] = useState(Array<Country>());
  const [fandom, setCountry] = useState(undefined);
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState(undefined);
  const [brand, setBrand] = useState(undefined);
  const [searchParams, setSearchParams] = useSearchParams();

  useEffect(() => {
    const getData = async () => {
      if(!!searchParams.get("fandoms")){
        // @ts-ignore
        setCountry(searchParams.get("fandoms"))
      }
      try {
        const response = await fetch(
          `http://127.0.0.1:8000/api/v1.0/cars/search/?a=b${!!searchParams.get("fandoms") ? "&fandoms=" + searchParams.get("fandoms") : ''}`
        );
        if (!response.ok) {
          throw new Error(
            `This is an HTTP error: The status is ${response.status}`
          );
        }
        let actualData = await response.json();
        setData(actualData);
        console.log(actualData)
        setError(null);
      } catch (err) {
        // @ts-ignore
        setError(err.message);
        setData([]);
      } finally {
        setLoading(false);
      }
    }
    getData()
  }, [])

  const changeCountry = async (event: SelectChangeEvent<Country>) => {
    event.preventDefault();
    // @ts-ignore
    setCountry(Number(event.target.value))
  };
  const changeCategory = async (event: SelectChangeEvent<String>) => {
    event.preventDefault();
    // @ts-ignore
    setCategory(event.target.value)
  };
  const changeBrand = async (event: SelectChangeEvent<String>) => {
    event.preventDefault();
    // @ts-ignore
    setBrand(event.target.value)
  };

  useEffect(() => {
    const fetchCountrys = async () => {
      try {
        const response = await fetch(
          `http://127.0.0.1:8000/api/v1.0/fandom/`
        );
        if (!response.ok) {
          throw new Error(
            `This is an HTTP error: The status is ${response.status}`
          );
        }
        let actualData = await response.json();
        setCountrys(actualData);
        console.log(actualData)
        setError(null);
      } catch (err) {
        // @ts-ignore
        setError(err.message);
        setCountrys([]);
      } finally {
        setLoading(false);
      }
    }
    fetchCountrys()
  }, [])

  async function handleSubmit() {
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/api/v1.0/cars/search/?a=b${!!search ? "&search=" + search : ''}${!!fandom ? "&fandoms=" + fandom : ''}${!!category ? "&category=" + category : ''}${!!brand ? "&brand=" + brand : ''}${!!searchParams.get("fandoms") ? "&fandoms=" + searchParams.get("fandoms") : ''}`
      );
      if (!response.ok) {
        throw new Error(
          `This is an HTTP error: The status is ${response.status}`
        );
      }
      let actualData = await response.json();
      setData(actualData);
      console.log(actualData)
      setError(null);
    } catch (err) {
      // @ts-ignore
      setError(err.message);
      setData([]);
    } finally {
      setLoading(false);
    }
  }

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline/>
      <NavBar/>
      <main style={{backgroundImage: `url(${background})`}}>
        <Box sx={{bgcolor: 'background.paper', pt: 8, pb: 6,}}>
          <Container maxWidth="md">
            <Typography component="h1" variant="h2" align="center" color="text.primary" gutterBottom>
              Поиск
            </Typography>
            <FormControl fullWidth margin="normal" size="small">
              <InputLabel id="demo-simple-select-label">Фандом</InputLabel>
              <Select labelId="demo-simple-select-label" id="demo-simple-select"
                      value={fandom} label="Age" onChange={changeCountry}>
                {fandoms.map((f: Country) => (
                  <MenuItem value={`${f.id}`}>{f.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <FormControl fullWidth margin="normal" size="small">
              <InputLabel id="demo-simple-select-label">Категория</InputLabel>
              <Select labelId="demo-simple-select-label" id="demo-simple-select"
                      value={category} label="Категория" onChange={changeCategory}>
                <MenuItem value="films">Фильмы</MenuItem>
                <MenuItem value="cars">Книги</MenuItem>
                <MenuItem value="games">Игры</MenuItem>
                <MenuItem value="anime">Аниме</MenuItem>
                <MenuItem value="cartoons">Мультфильмы</MenuItem>
              </Select>
            </FormControl>
            <FormControl fullWidth margin="normal" size="small">
              <InputLabel id="demo-simple-select-label">Жанр</InputLabel>
              <Select labelId="demo-simple-select-label" id="demo-simple-select"
                      value={brand} label="Жанр" onChange={changeBrand}>
                <MenuItem value="action">Экшен</MenuItem>
                <MenuItem value="adventure">Приключения</MenuItem>
                <MenuItem value="comedy">Комедия</MenuItem>
                <MenuItem value="drama">Драма</MenuItem>
                <MenuItem value="fantasy">Фэнтези</MenuItem>
                <MenuItem value="action">horror</MenuItem>
              </Select>
            </FormControl>
            <TextField margin="normal" fullWidth name="Поиск" label="Поиск" size="small" onChange={
              (value) => setSearch(value.target.value)
            }/>
            <Button fullWidth onClick={handleSubmit} variant="outlined" component="span" sx={{mt: 1}}>Найти</Button>
          </Container>
        </Box>
        <Container sx={{py: 8}} maxWidth="xl">
          <Grid container spacing={4}>
            {data.map((car: Car) => (
              <Grid item key="1" xs={12} sm={12} md={12}>
                <Card sx={{height: '100%', display: 'flex', flexDirection: 'column', p: 3}}>
                  <Grid container spacing={2}>
                    <Grid item xs={3} sx={{p: 2, textAlign: "center"}} alignItems="center">
                      <Box component="img" alt="The house from the offer." src={`${car.cover}`} alignItems="center"
                           sx={{
                             height: 360, width: 240,
                             maxHeight: {xs: 360, md: 300}, maxWidth: {xs: 240, md: 200}
                           }}
                      />
                    </Grid>
                    <Grid item xs={9} sx={{pl: 2, pb: 2, pr: 2}}>
                      <Typography component="h4" variant="h4" align="center" color="text.primary" sx={{pb: 3, py: 1}}>
                        {car.title}
                      </Typography>
                      <Typography align="left" color="text.primary" sx={{px: 3, pt: 1, maxHeight: {xs: 233, md: 167}}}>
                        <strong>Описание: </strong>{car.desc}
                      </Typography>
                      <Typography align="left" color="text.primary" sx={{px: 3, maxHeight: {xs: 233, md: 167}}}>
                        <strong>Автор: </strong>{car.author.username} ({car.author.first_name} {car.author.last_name})
                      </Typography>
                      <Typography align="left" color="text.primary" sx={{px: 3, maxHeight: {xs: 233, md: 167}}}>
                        <strong>Жанр: </strong>{car.brand}
                      </Typography>
                      <Typography align="left" color="text.primary" sx={{px: 3, maxHeight: {xs: 233, md: 167}}}>
                        <strong>Категория: </strong>{car.category}
                      </Typography>
                      <Typography align="left" color="text.primary" sx={{px: 3, maxHeight: {xs: 233, md: 167}}}>
                        <strong>Фандом: </strong>{car.fandoms.name}
                      </Typography>
                      <CardActions>
                        <Grid container justifyContent="flex-end">
                          <Link href={`/cars/view/${car.id}`}>
                            <Button size="medium">Читать</Button>
                          </Link>

                        </Grid>
                      </CardActions>
                    </Grid>

                  </Grid>
                </Card>
              </Grid>
            ))}


          </Grid>
        </Container>
      </main>
      <Box sx={{bgcolor: 'background.paper', p: 6}} component="footer">
        <Copyright/>
      </Box>
    </ThemeProvider>
  );
}
